import background from './img/background.png'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import { Container, Row, Col } from 'react-grid-system';
import ForumList from './ForumList';


const Blog = () => {
    const [forum, setForum] = useState(null);

    const handleDelete = (id) => {
        const newforum = forum.filter(foru => foru.id !== id)
        setForum(newforum)

    }

    useEffect(() => {
        fetch('http://localhost:8000/forum')
            .then(res => {
                return res.json();
            })
            .then(data => {
                setForum(data);
            })

    }, []);
    return ( 
        <div style={{
            backgroundImage: "url(" + background + ")",
            backgroundPosition: '0px 0px',
            backgroundSize: "cover",
            backgroundRepeat: "no-repeat",
            width: "100vw",
            height: "100vh",
          }}>
              <Container>
                  <Row>
                      <Col sm={6}>

                      </Col>
                      <Col sm={5}>
                          <Card style={{ width: '30rem', backgroundColor: 'orange' }} className='addcard'>
                              <Card.Body>
                                  <Card.Text>
                                      <h2>Forum de discussion</h2>
                                      {forum && <ForumList forum={forum} handleDelete={handleDelete} />}
                                    </Card.Text>
                                    <Button variant="primary">Go somewhere</Button>
                                </Card.Body>
                            </Card>
                        </Col>
                        <Col sm={1}>

                        </Col>
                    </Row>
                </Container>
              <h2>Blog</h2>
         </div>

     );
}

export default Blog;
